mapper.select:
SELECT a.NTT_ID, a.NTT_SJ, a.FRST_REGISTER_ID, IFNULL(b.USER_NM, a.NTCR_NM) as FRST_REGISTER_NM, DATE_FORMAT(a.FRST_REGIST_PNTTM, '%Y-%m-%d') as FRST_REGIST_PNTTM, a.RDCNT, a.PARNTSCTT_NO, a.ANSWER_AT, a.ANSWER_LC, a.USE_AT, a.ATCH_FILE_ID, a.BBS_ID, a.NTCE_BGNDE, a.NTCE_ENDDE FROM blog_post_tbl a LEFT OUTER JOIN COMVNUSERMASTER b ON a.FRST_REGISTER_ID = b.ESNTL_ID WHERE 1 = 1 AND a.BBS_ID = 'BLGPST_000000000001' AND a.USE_AT = #{useAt}
AND a.NTT_SJ LIKE CONCAT ('%', #{searchWrd},'%')
AND a.NTT_CN LIKE CONCAT ('%', #{searchWrd},'%')
AND b.USER_NM LIKE CONCAT ('%', #{searchWrd},'%')
ORDER BY a.SORT_ORDR DESC, NTT_NO ASC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.update:

UPDATE blog_post_tbl 
   SET NTT_NO = #{nttNo}
WHERE NTT_ID = #{nttId}


	UPDATE blog_post_tbl SET 
		NTT_NO = NTT_NO + 1
	WHERE 1 = 1 
    AND BBS_ID = 'BLGPST_000000000001'
	AND SORT_ORDR = #{sortOrdr}
	AND NTT_NO > #{nttNo}


	UPDATE blog_post_tbl 
	   SET NTT_NO = #{nttNo}
	WHERE 1 = 1 
    AND BBS_ID = 'BLGPST_000000000001' 
	AND NTT_ID = #{nttId}
